-- Tables

-- USER
CREATE TABLE Users (
    ID SERIAL PRIMARY KEY NOT NULL,
    Username VARCHAR(30) NOT NULL,
    Email VARCHAR(255) UNIQUE NOT NULL CHECK (Email LIKE '%_@__%.__%'),
    Password VARCHAR(16) NOT NULL CHECK (LENGTH(Password) BETWEEN 8 AND 16),
    Avatar VARCHAR(255) DEFAULT 'NOT FOUND'
);

-- PLAYLIST
CREATE TABLE Playlists (
    ID SERIAL PRIMARY KEY NOT NULL,
    Title VARCHAR(50) NOT NULL,
    UserID SERIAL NOT NULL,
    FOREIGN KEY (UserID) REFERENCES Users(ID),
    CONSTRAINT PlaylistsTitleUser UNIQUE (Title, UserID)
);

-- ARTIST
CREATE TABLE Artists (
    ID SERIAL PRIMARY KEY NOT NULL,
    Name VARCHAR(30) NOT NULL,
    Email VARCHAR(255) UNIQUE NOT NULL CHECK (Email LIKE '%_@__%.__%'),
    Password VARCHAR(16) NOT NULL CHECK (LENGTH(Password) BETWEEN 8 AND 16),
    Artist_Image VARCHAR(255) DEFAULT 'NOT FOUND',
    Biography TEXT DEFAULT ':)',
    Verified BOOLEAN DEFAULT FALSE,
    Language VARCHAR(20) DEFAULT 'unspecified'
);

-- SONG
CREATE TABLE Songs (
    ID SERIAL PRIMARY KEY NOT NULL,
    Title VARCHAR(50) NOT NULL,
    Youtube_Link VARCHAR(255) UNIQUE NOT NULL,
    Release_Date DATE DEFAULT CURRENT_DATE,
    Lyrics VARCHAR(8000) NOT NULL,
    Genre VARCHAR(20) DEFAULT 'Other',
    Duration TIME NOT NULL,
    ArtistID SERIAL NOT NULL,
    FOREIGN KEY (ArtistID) REFERENCES Artists(ID),
    CONSTRAINT SongsTitleArtist UNIQUE (Title, ArtistID)
);

-- ALBUM
CREATE TABLE Albums (
    ID SERIAL PRIMARY KEY NOT NULL,
    Title VARCHAR(50) NOT NULL,
    Album_Cover_Image VARCHAR(255) DEFAULT 'NOT FOUND',
    Release_Date DATE DEFAULT CURRENT_DATE,
    Genre VARCHAR(20) DEFAULT 'Other',
    ArtistID SERIAL NOT NULL,
    FOREIGN KEY (ArtistID) REFERENCES Artists(ID),
    CONSTRAINT AlbumsTitleArtist UNIQUE (Title, ArtistID)
);

-- TABLES FOR THE MANY-TO-MANY RELATIONSHIPS
CREATE TABLE LikedSongs (
    UserID SERIAL,
    SongID SERIAL,
    PRIMARY KEY (UserID, SongID),
    FOREIGN KEY (UserID) REFERENCES Users(ID),
    FOREIGN KEY (SongID) REFERENCES Songs(ID)
);

CREATE TABLE LikedAlbums (
    UserID SERIAL,
    AlbumID SERIAL,
    PRIMARY KEY (UserID, AlbumID),
    FOREIGN KEY (UserID) REFERENCES Users(ID),
    FOREIGN KEY (AlbumID) REFERENCES Albums(ID)
);

CREATE TABLE SongsInPlaylists (
    SongID SERIAL,
    PlaylistID SERIAL,
    PRIMARY KEY (SongID, PlaylistID),
    FOREIGN KEY (SongID) REFERENCES Songs(ID),
    FOREIGN KEY (PlaylistID) REFERENCES Playlists(ID)
);

CREATE TABLE SongsInAlbums (
    SongID SERIAL,
    AlbumID SERIAL,
    PRIMARY KEY (SongID, AlbumID),
    FOREIGN KEY (SongID) REFERENCES Songs(ID),
    FOREIGN KEY (AlbumID) REFERENCES Albums(ID)
);



